pragma solidity^0.5.16;

contract Procurement{
    address owner;
    address manager;
    uint256 tenderAdderess;
    uint256 tenderId;

    
    struct Bid{
        address owner;
        uint amount;
        bytes32 email;
        string desc; 
    }
    Bid[] private bids;

    struct Tender {
        address manager;
        uint id;
        bytes32 name;

    }
    
    
    mapping(uint => Tender) tenders;

    
    
    uint256 latestId = 0;

    uint latestTenderId = 0;
  
    function createTender(bytes32 _name) public returns(uint) {
        latestTenderId++;
        manager = msg.sender;

        tenders[latestTenderId] = Tender(manager, latestTenderId, _name);

        return latestTenderId;
    }
    function getTender(uint _id)public view returns(address, uint, bytes32) {
       return( 
           tenders[_id].manager,
           tenders[_id].id,
           tenders[_id].name
           );
        


    }
    function getBid()public view returns(address, uint) {
        return (
            owner,
            bids.length
        );
    }
    function setBid(uint _amount, bytes32 _email, string memory _desc) public {
        Bid memory newBid = Bid({
            owner: msg.sender,
            amount: _amount,
            email: _email,
            desc: _desc
        });
        bids.push(newBid);
        
        

    }
}
